import React from "react";
import { useState } from "react";
import axios from "axios";

export default function Display() {
  // state for targetting input user data and sending to backend
  const [userInput, setInput] = useState("");

  // innerHTML for input
  const inputDiv = document.getElementById("input");

  // innerHTML for output
  const outputDiv = document.getElementById("output");

  // collecting user input data
  function getInput(e) {
    // console.log(e.target.value);
    setInput(() => {
      return { [e.target.name]: e.target.value };
    });
  }
  // submit user input data to backend
  const sendInput = (e) => {
    // e.preventDefault();
    console.log(userInput);

    axios
      .post("http://localhost:8080/Display/Display", userInput)
      .then((data) => {
        console.log("Success:", data);
        inputDiv.innerHTML = "Succes";
      })
      .catch((error) => {
        console.error("Error:", error);
        outputDiv.innerHTML = "Error";
      });
  };
  // retrieve greeting from data using fetch get request
  const getGreeting = () => {
    // console.log(5);
    fetch("http://localhost:8080/Display/Display")
      .then((res) => res.json())
      .then((resultsData) => {
        console.log(resultsData);
        outputDiv.innerHTML = resultsData[0].message;
      });
  };
  return (
    <div>
      <h1>Send Message</h1>
      <input
        type="text"
        name="message"
        onChange={getInput}
      ></input>
      {/* displaying confirmation text on data sent to backend  */}
      <div id="input"></div>
      {/* submitting input data to backend  */}
      <button onClick={sendInput}>
        Send the message
      </button>
      <br></br>
      {/* button used for retreiving user input data from database  */}
      <button onClick={getGreeting}>
        Get Message
      </button>
      {/* display retreived greeting data  */}
      <div id="output"></div>
    </div>
  );
}
